import React from 'react';

const Weather = props => {
  return (
    <div>
      { props.city &&
      <div>
        <p>Местоположение: <span>{props.city}</span>, <span>{this.props.country}</span></p>
        <p>Температура: <span>{props.temp}</span></p>
        <p>Давление: <span>{props.pressure}</span></p>
        <p>Заход солнца: <span>{props.sunset}</span></p>
      </div>
      }
      <p>{ props.error }</p>
    </div>
  )
};

export default Weather;
